import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AddSubject = () => {
    const [subject, setSubject] = useState({
        sub_name: '',
        sub_code: '',
        branch: '',
        sem: '',
        sub_category_id: ''
    });

    const [subCategories, setSubCategories] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    // Fetch sub-categories when component mounts
    useEffect(() => {
        const fetchSubCategories = async () => {
            try {
                const response = await axios.get('/api/sub_categories');
                setSubCategories(response.data);
            } catch (err) {
                setError('Failed to fetch sub-categories');
                console.error(err);
            }
        };

        fetchSubCategories();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');
        setSuccess('');

        try {
            const response = await axios.post('/api/subjects/add', subject);
            setSuccess('Subject added successfully!');
            // Clear form
            setSubject({
                sub_name: '',
                sub_code: '',
                branch: '',
                sem: '',
                sub_category_id: ''
            });
        } catch (err) {
            setError(err.response?.data?.message || 'Failed to add subject');
        } finally {
            setLoading(false);
        }
    };

    const handleChange = (e) => {
        setSubject({ ...subject, [e.target.name]: e.target.value });
    };

    return (
        <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded-lg shadow-md">
            <h2 className="text-2xl font-bold mb-6">Add Subject</h2>

            {error && (
                <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
                    {error}
                </div>
            )}

            {success && (
                <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">
                    {success}
                </div>
            )}

            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label className="block text-sm font-medium text-gray-700">Subject Name</label>
                    <input
                        type="text"
                        name="sub_name"
                        value={subject.sub_name}
                        onChange={handleChange}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                        required
                    />
                </div>

                <div>
                    <label className="block text-sm font-medium text-gray-700">Subject Code</label>
                    <input
                        type="text"
                        name="sub_code"
                        value={subject.sub_code}
                        onChange={handleChange}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                        required
                    />
                </div>

                <div>
                    <label className="block text-sm font-medium text-gray-700">Branch</label>
                    <input
                        type="text"
                        name="branch"
                        value={subject.branch}
                        onChange={handleChange}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                        required
                    />
                </div>

                <div>
                    <label className="block text-sm font-medium text-gray-700">Semester</label>
                    <input
                        type="text"
                        name="sem"
                        value={subject.sem}
                        onChange={handleChange}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                        required
                    />
                </div>

                <div>
                    <label className="block text-sm font-medium text-gray-700">Subject Category</label>
                    <select
                        name="sub_category_id"
                        value={subject.sub_category_id}
                        onChange={handleChange}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                        required
                    >
                        <option value="">Select a category</option>
                        {subCategories.map((category) => (
                            <option key={category._id} value={category._id}>
                                {category.scheme} - {category.name} ({category.code})
                            </option>
                        ))}
                    </select>
                </div>

                <button
                    type="submit"
                    disabled={loading}
                    className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50"
                >
                    {loading ? 'Adding...' : 'Add Subject'}
                </button>
            </form>
        </div>
    );
};

export default AddSubject;
